module.property-provider:
var ua = $wnd.navigator.userAgent.toLowerCase();
if (ua.indexOf("macintosh") != -1 || ua.indexOf("mac os x") != -1) {
  return "mac";
}
if (ua.indexOf("linux") != -1) {
  return "linux";
}
if (ua.indexOf("windows") != -1 || ua.indexOf("win32") != -1) {
  return "windows";
}
return "linux";
