function toggleMF(e){
    e?e.preventDefault():"";
    var t=$(e.target)
    var p=t.parent();
    var s=$($get("hm_widget_form"));
    var c=e.target.innerHTML;
    var isFirst=p.hasClass("first-node")
    var isLast=p.hasClass("last-node")
    if(c=='Add'){
        if(!isFirst){
            e.target.innerHTML="Remove";
        }
        p.next().show()
        t.hide()
    }else if(c=='Remove'){
        if(!isLast){
            e.target.innerHTML="Add"
        }
        p.hide()
        p.prev().find(".addremove").show()
    }
}
