function showFontConfigurationDialog(){var B=MathJax.Hub.config["HTML-CSS"].webFont;var D="<div>Choose MathJax Font</div><div>Current font: "+B+"</div><select id='font-choose'><option></option><option>Asana-Math</option><option>Gyre-Pagella</option><option>Gyre-Termes</option><option>Latin-Modern</option><option>Neo-Euler</option><option>STIX-Web</option><option>TeX</option></select>";D+="<div style='margin-top: 15px'><button>Close</button></div>";var A="<div id='font-config' style='padding: 10px; position: absolute;top: 10px;left:10px;width: 250px;height: 100px;border: 1px solid blue;background: orange;'>"+D+"</div>";var C=document.createElement("div");C.innerHTML=A;document.body.appendChild(C);$("#font-config select").value=B;$("#font-config button").click(function(E){C.remove()});$("#font-config select").change(function(E){var F=window.location.href;var G=F.indexOf("&mj_font");if(G>-1){F=F.substring(0,G)}F+="&mj_font="+E.target.value;window.location.href=F})}function setupMathJax(){var B=window.location.href;var C=B.indexOf("&mj_font");var A=null;if(C>-1){A=B.substring(C+9);console.log("requesting MathJax font: "+A)}else{A="STIX-Web"}MathJax.Hub.Config({showMathMenu:true,"HTML-CSS":{webFont:A,preferredFont:"STIX-Web",mtextFontInherit:true,matchFontHeight:false}})}function processMathJax(){var A=$get("tutor_raw_steps_wrapper");if(!A){console.log("processing mathjax on page");MathJax.Hub.Queue(["Typeset",MathJax.Hub])}else{console.log("processing mathjax on tutor");MathJax.Hub.Queue(["Typeset",MathJax.Hub],A);MathJax.Hub.Queue(function(){console.log("MathJax Complete")})}};